{
    "name": "google",
    "description": "IdP for Google Federation",
    "image": "https://localhost:9443/console/resources/connections/assets/images/logos/google.svg",
    "isPrimary": false,
    "isFederationHub": false,
    "homeRealmIdentifier": "localhost",
    "certificate": {
        "certificates": [],
        "jwksUri": "https://localhost:9444/oauth2/jwks"
    },
    "alias": "https://localhost:9444/oauth2/token",
    "claims": {
        "userIdClaim": {
            "uri": "http://wso2.org/claims/username"
        },
        "roleClaim": {
            "uri": "http://wso2.org/claims/username"
        },
        "mappings": [
            {
                "idpClaim": "country",
                "localClaim": {
                    "uri": "http://wso2.org/claims/username"
                }
            }
        ],
        "provisioningClaims": [
            {
                "claim": {
                    "uri": "http://wso2.org/claims/username"
                },
                "defaultValue": "sathya"
            }
        ]
    },
    "roles": {
        "mappings": [
            {
                "idpRole": "google-manager",
                "localRole": "manager"
            }
        ],
        "outboundProvisioningRoles": [
            "admin"
        ]
    },
    "federatedAuthenticators": {
        "defaultAuthenticatorId": "VHdpdHRlckF1dGhlbnRpY2F0b3I",
        "authenticators": [
            {
                "authenticatorId": "VHdpdHRlckF1dGhlbnRpY2F0b3I",
                "isEnabled": true,
                "isDefault": false,
                "properties": [
                    {
                        "key": "somePropertyKey",
                        "value": "somePropertyValue"
                    }
                ]
            }
        ]
    },
    "provisioning": {
        "jit": {
            "isEnabled": true,
            "scheme": "PROVISION_SILENTLY",
            "userstore": "PRIMARY",
            "associateLocalUser": true,
            "attributeSyncMethod": "OVERRIDE_ALL"
        },
        "outboundConnectors": {
            "defaultConnectorId": "U0NJTTI",
            "connectors": [
                {
                    "connectorId": "U0NJTTI",
                    "isEnabled": true,
                    "isDefault": false,
                    "blockingEnabled": false,
                    "rulesEnabled": false,
                    "properties": [
                        {
                            "key": "somePropertyKey",
                            "value": "somePropertyValue"
                        }
                    ]
